actionConfiguration.body:
async ()=> {
			if(vayMultipleCurrencySelection.selectedValues.length > 0) {
				storeValue('vayCCY',vayMultipleCurrencySelection.selectedValues);

				for (var i = 0; i < vayMultipleCurrencySelection.options.length; i++) {
					const currencyCode = vayMultipleCurrencySelection.options[i].value;
					if(vayMultipleCurrencySelection.selectedValues.includes(currencyCode)) {
						await JSBankAccountcurrencyDetails.insertIfNotExist(currencyCode);			
					} else {
						await JSBankAccountcurrencyDetails.removeIfExist(currencyCode);
					}
				}		

				await selectCurrentSelectedCurrency.run();	
				showAlert('Currencies Details updated.');

			} else {
				storeValue('vayCCY',selectCurrentSelectedCurrency.data.map((i)=> i.value));
			}

			navigateTo('Bank Account Information List', {});
}

