async () => {
		const userRole = appsmith.store.userRole;
		const status = userRole === 'Maker' ? 's' :  Select1.selectedOptionValue;
		const comment = userRole === 'Maker' ? Input1.text : Input3.text;

		const record = listDDQuery.data.find((item) => item.VERIFIED_BY_ROLE === userRole);

		if(!record) {
			showAlert('Insert');
			await insertDDQuery.run(undefined, undefined, {
				status, comment
			});
		} else {
			showAlert('Update');
			await updateDDQuery.run(undefined, undefined, {
				id: record.ID, status, comment
			});
		}

		navigateTo('Official Use Only');
	}

