async () => {
		showAlert('In change event function');

		const oldFilePath = JSObjectUploadKYC.getOldFilePath('POI');

		if(oldFilePath !== null) {
			await DeleteFileQuery.run(undefined, undefined, {
				path: oldFilePath
			});
		}
		await	QueryInsertEntityKYCFGN.run({ docType: vayProofofidentity.text, docPathFilename : selectFilesProofOfIdentity.files[0].name});
		const interval = setInterval(() => {
			ListKYCDocQuery.run();
			clearInterval(interval);
		}, 2000);

		await QueryPOI.run();
	}

