async (keyPId, keyPType) => {
		const indKYCData = await ListIndKYCDocQuery.run();
    const mappedData = indKYCData.map((entry) => {
      const dirs = entry.fileName.split("/");
      const fileDetails = dirs[2].split("__");
      // kycfiles/0df5b01a_IndIndiaKYC_3b0ah603/PHT__Logo.png
      return {
        DocumentType: CNXQueryJSObject.docShortForms[fileDetails[0]],
        DocumentName: fileDetails.length === 3 ? fileDetails[1] : "-",
        FileName: fileDetails[fileDetails.length - 1],
        path: entry.fileName,
        docTypeCode: fileDetails[0],
        docName: fileDetails.length === 3 ? fileDetails[1] : null,
        url: entry.url,
        keyPId: dirs[1].split("_")[2],
        keyPType: keyPType,
      };
    });
    return mappedData.filter((item) => item.keyPId === keyPId);
  }

