async () => {
		JSObject1.onSaveClick();
		if(appsmith.store.userRole == 'Maker')
			{
				const response =  updateStatusAuthorised.run(); 
	  if(!!response){
		 	showAlert('Customer  application Status updated :: In Authorised');

	   }else{
		  showAlert('Customer application record updation failed...Contact Admin')
	 	}
			}
		if(appsmith.store.userRole == 'Checker')
			{
				const response =  await updateStatusCustomerCreation.run(); 
	  if(!!response){
		 	showAlert('Customer  application Status updated :: In Customer Creation');
			const cnxQueryResponse = await CNXQueryJSObject.runCNXQueries();
			if(cnxQueryResponse) {
				const statusResponse = await updateStatusCustomerRegistered.run();
				if(!!statusResponse){
		 			showAlert('Customer application Status updated :: In Customer Registered');	
				} else {
		  		showAlert('Customer application record updation failed...Contact Admin');
	 			}
			}
	   }else{
		  showAlert('Customer application record updation failed...Contact Admin')
	 	}
			}
		navigateTo('Homepage');
		//write code here
	}

