export default {
    get_data: (personOID) => {
        const formFields = List1.items;
        //const personOID = 3;
        return formFields.map((item, idx) => {
            return `(
						  '${utils.generateOID(20)}',
							'${personOID}',
							'${personOID}',
							'${appsmith.store.customerOID}',
							'${appsmith.store.customerID}',
							'${'Key Person Foreign'}',
							'${item.Text8.text[idx]}',
							${item.vayIndCKYCNO.text}
							)`;
        }).join(',')
    },
    dummy: () => {
			console.log(appsmith.store)
        const formFields = List1.items;
        return formFields.map((item, idx) => {
            return `'${item.Text8.text[idx]}',
			${(item.vayGuardRel.text)},
			${(item.vayCurrencyLimitOID.text)}`;
        });
    },
    updateKeyPersonCurrencyLimitold: () => {
        const formFields = List1.items;
        return formFields.map((item, idx) => {
            return `UPDATE APS_CUSTOMER_KEY_PERSON_CURRENCYLIMIT SET CUSTOMER_KEY_PERSON_CURRENCY_LIMIT ='${(item.vayGuardRel.text)}' WHERE OID = '${(getKeyPersonCurrencyLimit.data[idx].OID)}'`
            //return formFields
        });
    },
    formList: () => {
        const formFields = List1.items;
        return formFields;
    },
    getKeyPersonData: () => {
        showAlert('Key Person' + appsmith.store.keyPersonData)
        //if(appsmith.store.keyPersonData== undefined){
        const retain_keyPersonData = Array()
        showAlert('retain_keyPersonData -' + retain_keyPersonData[0])
        storeValue('keyPersonData', retain_keyPersonData)
        //	}else{

        //}

        return appsmith.store.keyPersonData;
    },
    getKeyPersonCurrencyLimitData: () => {
        console.log('Key Person Limit Currencies fecthed :', getKeyPersonCurrencyLimit.data);
        if (getKeyPersonCurrencyLimit.data.length > 0) {
            return getKeyPersonCurrencyLimit.data;
        } else {
            const selectedCurrency = appsmith.store.vayCCY;
            console.log('In Static Currencies', selectedCurrency);
            return appsmith.store.vayCCY.map((item) => {
                return ({
                    CUSTOMER_KEY_PERSON_CURRENCY_CODE_FORADMTXN: item,
                    CUSTOMER_KEY_PERSON_CURRENCY_LIMIT: 0
                });
            });
        }
    },
    updateKeyPersonCurrencyLimitQuery: async () => {
        const updates = this.updateKeyPersonCurrencyLimitString();
        updates.forEach((updatedItem) => {
            console.log('updatedItem-', updatedItem);
            //const return_currencyresponse = updateKeyPersonCurrencyLimit.run({currencyLimitOID:updatedItem.oid, currencyLimit:updatedItem.limit});
            const return_currencyresponse = updateKeyPersonCurrencyLimit.run({ currencyLimitOID: updatedItem.oid, currencyLimit: updatedItem.limit });
            if (!!return_currencyresponse) {
                console.log("Success", return_currencyresponse);
            } else {
                console.log("Error", return_currencyresponse);
            }
        });
    },
    getCurrencies: () => {
        return appsmith.store.vayCCY;
    },
    updateKeyPersonCurrencyLimitString: () => {
        const formFields = List1.items;
        //console.log('Form List',formFields);
        return formFields.map((item, idx) => {
            return ({
                limit: item.vayGuardRel.text,
                oid: getKeyPersonCurrencyLimit.data[idx].OID
            });
        });
    },
    getStore: () => {
        return appsmith.store;
    },
    changeKeyPersonAddress: async () => {
        //const retain_keyPersonData = await Helper.retainKeyPersonInKey();
        const retain_keyPersonData = Helper.initializeRetainKPObject();
        await storeValue('keyPersonData', retain_keyPersonData);
        console.log('Entity Admin Data - ', appsmith.store.keyPersonData);
        if (vayFgnKeyPersonAddCountrySelec.selectedOptionValue != "IN") {
            navigateTo('Add New Address Indian', { "bkPage": "FgnKpAdd" });
        }
    },
    initializeRetainKPObject: () => {
        const retain_keyPersonData = {}
        retain_keyPersonData.CUSTOMER_KEY_PERSON_ENTITY_TYPE = 'Key Person Foreign'
				retain_keyPersonData.CUSTOMER_KEY_PERSON_TYPE = vayKeyPersonFgnType.selectedOptionValue
				//showAlert('Key Person Tyep'+ retain_keyPersonData.CUSTOMER_KEY_PERSON_TYPE)
			  retain_keyPersonData.CUSTOMER_KEY_PERSON_IS_AUTH_SIGN = vayisMinor.isChecked
        retain_keyPersonData.CUSTOMER_KEY_PERSON_OTHERS_INFO = vaypartnerperct.text
        retain_keyPersonData.CUSTOMER_KEY_PERSON_PARTNER_PERC = vayOwnershipPerct.text
        retain_keyPersonData.CUSTOMER_KEY_PERSON_FIRSTNAME = vayDesignationOthers.text
        retain_keyPersonData.CUSTOMER_KEY_PERSON_MIDDLENAME = vayDinDpin.text
        retain_keyPersonData.CUSTOMER_KEY_PERSON_LASTNAME = vayFname.text
        retain_keyPersonData.CUSTOMER_KEY_PERSON_MOTHERNAME = vayMName.text
        retain_keyPersonData.CUSTOMER_KEY_PERSON_FATHERNAME = vayLname.text
        retain_keyPersonData.CUSTOMER_KEY_PERSON_DOB = moment(vayIndDOB.selectedDate).format("YYYY-MM-DD")
        retain_keyPersonData.CUSTOMER_KEY_PERSON_TAX_ID = Input7.text
        retain_keyPersonData.CUSTOMER_KEY_PERSON_NATIONALITY = Select3.selectedOptionValue
        retain_keyPersonData.CUSTOMER_KEY_PERSON_RES_TAX_PURPOSE = Select4.selectedOptionValue
        //retain_keyPersonData.CUSTOMER_KEY_PERSON_DESIGNATION  = ''
        retain_keyPersonData.CUSTOMER_KEY_PERSON_PASSPORT_NO = Input21.text
        retain_keyPersonData.CUSTOMER_KEY_PERSON_PASSPORT_EXPIRE_DATE = moment(vayIndDOB.selectedDate).format("YYYY-MM-DD")
        retain_keyPersonData.CUSTOMER_KEY_PERSON_ACCESS_AS = vayKeyPersonFgnUserAccess.selectedOptionValue
        retain_keyPersonData.CUSTOMER_KEY_PERSON_DIN_DPIN = Input8.text
        retain_keyPersonData.CUSTOMER_KEY_PERSON_ADDRESS_LINE1 = vayIndAddrLin1.text
        retain_keyPersonData.CUSTOMER_KEY_PERSON_ADDRESS_LINE2 = vayIndAddrLin2.text
        retain_keyPersonData.CUSTOMER_KEY_PERSON_ADDRESS_CITY = vayIndCityLocality.text
        retain_keyPersonData.CUSTOMER_KEY_PERSON_ADDRESS_DISTRICT = vayIndCityDistrict.text
        retain_keyPersonData.CUSTOMER_KEY_PERSON_ADDRESS_STATE = vayIndStateCountry.text
        retain_keyPersonData.CUSTOMER_KEY_PERSON_ADDRESS_COUNTRY = vayFgnKeyPersonAddCountrySelec.selectedOptionValue
        retain_keyPersonData.CUSTOMER_KEY_PERSON_ADDRESS_ZIP = Input15.text
        retain_keyPersonData.CUSTOMER_KEY_PERSON_MOBILE = vayPassportNo.text
        retain_keyPersonData.CUSTOMER_KEY_PERSON_EMAIL = vayGovtIssueId.text
        appsmith.store.vayCCY.map((item) => {
            return ({
                CUSTOMER_KEY_PERSON_CURRENCY_CODE_FORADMTXN: item,
                CUSTOMER_KEY_PERSON_CURRENCY_LIMIT: vayIndCKYCNO.text
            });
        });
        return retain_keyPersonData
    },
    clearappsmithStore: () => {
        for (let key in appsmith.store) {
            if (appsmith.store.hasOwnProperty(key)) {
                const value = appsmith.store[key];
                if (key == "keyPersonData") {
                    storeValue(key, '')
                }
                console.log(key, value);
            }
        }
    },
	fnCheckIfDINRequired: () => {
		const keyType = vayKeyPersonFgnType.selectedOptionValue;
		if(keyType === 'director' || keyType === 'managing-director' || keyType === 'whole-time-director'){
			return true;
		}
		if(keyType === 'partner' && appsmith.store.vayFgnConstitution && 		appsmith.store.vayFgnConstitution.toLowerCase().trim() === 'llp'){
			return true;
		}
		return false;
	},

}
