async () => {
		const userRole = appsmith.store.userRole;
		const status = userRole === 'Maker' ? Select1.selectedOptionValue : Select2.selectedOptionValue;
		const comment = userRole === 'Maker' ? Input1.text : '';
		const formData = VerificationJSObject.getFormRecord(userRole);
		if(formData) {
			await showAlert(''+formData.ID);
			showAlert('Update');
			await updateDocVerStatus.run(null, null, {
				id: formData.ID,
				comment, status
			});
		} else {
			showAlert('Insert');
			await insertDocVerStatus.run(null, null, {
				comment, status
			});
		}
		showAlert('Status Updated!');
		navigateTo('Verification Individual Summar', {});
	}

