jsonPathKeys:
() => {
		return ListKYCDocQuery.data.map((entry) => {
			const dirs = entry.fileName.split("/");
			const fileDetails = dirs[2].split("__");
			return {
				DocumentType: VerificationJSObject.docShortForms[fileDetails[0]],
				DocumentName: fileDetails.length === 3 ? fileDetails[1] : '-',
				FileName: fileDetails[fileDetails.length - 1],
				Download: '',
				ApiVerificaion: '',
				MakerVerificationStatus: VerificationJSObject.getMakerStatus(fileDetails[0], 'Maker'),
				CheckerVerificationStatus: VerificationJSObject.getMakerStatus(fileDetails[0], 'Checker'),
				path: entry.fileName,
				docTypeCode: fileDetails[0],
				docName: fileDetails.length === 3 ? fileDetails[1] : null,
				verFilePrefix: entry.fileName.split("__")[0].replace("_", "_VER_")
			};
		});
	}
